import React, { useState } from 'react';
import axios from 'axios';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [error, setError] = useState('');

 const handleLogin = async () => {
  try {
    const response = await API.post('/login/', { username, password, token });
    console.log(response.data);
    localStorage.setItem('access_token', response.data.access); // <--- Save token
    setError('');
    // Redirect user to dashboard or 2FA page if you want
  } catch (err) {
    setError(err.response?.data?.error || "Login failed.");
  }
};

  return (
    <div style={{ textAlign: 'center', marginTop: '100px' }}>
      <h1>Login</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <input placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      </div>
      <div>
        <input placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <div>
        <input placeholder="2FA Token" value={token} onChange={(e) => setToken(e.target.value)} />
      </div>
      <button onClick={handleLogin} style={{ marginTop: '10px' }}>Login</button>
    </div>
  );
};

export default LoginPage;
